import React from 'react';
import {Button, Navbar, Nav, NavDropdown, 
  Container, Col, Row, 
  Card, FormControl, Form} from 'react-bootstrap'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import logo from './logo.svg';
import './App.css';

class Top extends React.Component{
  render(){
    return(
      <div>
        <Navbar bg="success" variant="light" expand="lg">
          <Navbar.Brand href="/"><i class="fas fa-flask"></i> NW Labs</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mr-auto">
              <Nav.Link href="/about">About</Nav.Link>
              <Nav.Link href="/help">Help</Nav.Link>
            </Nav>
            <NavDropdown title="Account" id="collasible-nav-dropdown">
              <NavDropdown.Item href="#action/3.1">Login</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">Sign Up</NavDropdown.Item>
            </NavDropdown>
          </Navbar.Collapse>
        </Navbar>
      </div>
    )
  }
}

class Left extends React.Component{
  render(){
    return(
      <div>
        <Nav defaultActiveKey="/home" className="flex-column">
          <Nav.Link href="/">Home</Nav.Link>
          <Nav.Link href="/about">About</Nav.Link>
          <Nav.Link href="/help">Help</Nav.Link>
          <Nav.Link eventKey="disabled" disabled>
            Disabled Link
          </Nav.Link>
        </Nav>
      </div>
    )
  }
}

class Right extends React.Component{
  render(){
    const assay = [
      {
        id: 1234,
        compound: "Acetaldehyde",
        customer: "Ace Chemical",
        status: "testing"
      },
      {
        id: 2345,
        compound: "Chloroacetic acid",
        customer: "BioLabs",
        status: "testing"
      },
      {
        id: 3456,
        compound: "Potassium nitrite",
        customer: "Chemical Solutions",
        status: "in queue"
      },
      {
        id: 4567,
        compound: "Hydrocloric acid",
        customer: "TechBio Co.",
        status: "arrived"
      }
    ];
    const assayList = assay.map((assay) => 
    <div>
      <li>
        <b>ID:</b> {assay.id}<br/>
        <b>Compound:</b> {assay.compound}<br/>
        <b>Status:</b> {assay.status}
      </li>
      <br/>
    </div>)
    return(
      <div>
        <br/>
        <h6>Recent Assays</h6>
        <ul style={{fontSize: "0.25rem", paddingLeft: "0", paddingTop: "2rem"}}>{assayList}</ul>
      </div>
    )
  }
}

class HomeHeader extends React.Component{
  render(){
    return(
      <header className="App-header">
        <div>
          <i className="fas fa-flask m-3 p-4" style={{
            fontSize: "10rem",
            color: "#blue"
          }}></i></div>
        <div>
          <h1>Northwest Labs</h1>

          <h3>Where all you chemical testing dreams come true!</h3>
        </div>
        <Button variant="success" size="lg" style={{margin: "2rem"}}>Get Started today!</Button>
      </header>
    )
  }
}
class AboutHeader extends React.Component{
  render(){
    return(
      <header className="App-header">
        <div>
