import React, {useState} from 'react'
import {useRouteMatch} from "react-router-dom";
import PRODUCTS from '../products'

// function ChangeState(int){
//     this.setState((state) =>{
//         return{imgState: state.imgState = int}
//     });
//     console.log(this.state)
// }

function ProductDetails(props){
    //filter the Array and get the product that we are displaying details for
    let ProdArray = Object.values(PRODUCTS)

    const match = useRouteMatch()

    if(match){
        ProdArray = ProdArray.filter(prod => {
            return prod.id === match.params.id
        })
    }

    let prod = ProdArray[0]
    //Changing the picture using React State 
    const [ind, setPicInd] = useState(1);

    return(
        <div className="flex" style={{height: '100vh'}}>
            <br/>
            <br/>
            <br/>
            <h1>{prod.name}</h1>
            <div className="row">
                <div className="col-8" style={{textAlign: 'left'}}>
                    <p className="p-5 text-justify">
                        {prod.description}
                    </p>
                </div>
                <div className="col-2">
                    <div className="row">
                        <img src={"/media/products/" + prod.filename + "-" + ind + ".png"} alt="main product"/>
                    </div>
                    <div className="row" style={{display: 'flex', justifyContent: 'center'}}>
                        <div className="col-sm-2 m-2">
                            <img height="50rem" onMouseOver={() => setPicInd(1)} className="border" src={"/media/products/" + prod.filename + "-1.png"} alt="product 1"/>
                        </div>
                        <div className="col-sm-2 m-2">
                            <img height="50rem" onMouseOver={() => setPicInd(2)} className="border" src={"/media/products/" + prod.filename + "-2.png"} alt="product 2"/>
                        </div>
                        <div className="col-sm-2 m-2">
                            <img height="50rem" onMouseOver={() => setPicInd(3)} className="border" src={"/media/products/" + prod.filename + "-3.png"} alt="product 3"/>
                        </div>
                        <div className="col-sm-2 m-2">
                            <img height="50rem" onMouseOver={() => setPicInd(4)} className="border" src={"/media/products/" + prod.filename + "-4.png"} alt="product 4"/>
                        </div>
                    </div>
                </div>
            </div>


        </div>
    )
}

export default ProductDetails
