import React from 'react'
import {
    Container, Col, Row, 
    CardDeck} from 'react-bootstrap'
    import {
        BrowserRouter as Router,
        Switch,
        Route,
        useRouteMatch
      } from "react-router-dom";
import ProductCard from '../ProductCard'

const match = useRouteMatch()

class HomeHeader extends React.Component{
    render(){
        return(
            <header className="App-header" style={{backgroundColor: "light grey"}}>
                <div>
                    <i className="fas fa-cash-register m-3 p-4" style={{
                        fontSize: "10rem",
                        color: "#FAF9F9"
                    }}></i>
                </div>
                <div>
                    <h1>General Store Products</h1>

                    <h3>Here are our slue of amazing things!</h3>
                </div>
            </header>
        )
    }
 };

class Home extends React.Component{

render(){
    return(
        <div>
            <Router>
                <HomeHeader/>
                <Container>
                    <Row>
                    <Col>
                        <h1>Products Page</h1>
                        <p>We have the best stuff.</p>
                    </Col>
                    </Row>
                    <Row>
                    {/* Products filtered deck */}
                        <CardDeck>
                            {this.props.products
                            .filter(prod => { return prod.category === match.params.slug})
                            .map((p) => {
                                return (
                                    <Col xs={3}>
                                        <ProductCard product={p} key={p.id}/>
                                    </Col>
                                )
                            })}
                        </CardDeck>
                    </Row>
                    <Row>
                    <br></br>
                    </Row>
                </Container>
            </Router>
        </div>
        )
    }
};

export default Home;
