import React, { useContext } from 'react'
import {Nav} from 'react-bootstrap'
import {Link} from 'react-router-dom'
//import PRODUCTS from '../products';
import AppContext from '../context'



export default function Left(props){

  const state = useContext(AppContext)

  const categories = {}
  // for (let c of Object.values(state.categories)){
  //   console.log(c)
  // }

  for (let p of Object.values(state.products)){
    if(p.category in state.categories.id){
      //categories[p.category] = categories[p.category] + 1
      console.log('CODE')
    }
    else{
      //categories[p.category] = 1
    }
  }

  return(
    <div> 
      <Nav defaultActiveKey="/home" className="flex-column">
        <Link to="/" className="nav-link">
          All Products ({Object.values(state.products).length})
        </Link>
        <Nav.Item>
          {Object.entries(state.categories).map((c => (
              <Link to={'/category/' + c[1].title} key={c[0]} className="nav-link">
                  {c[1].title} ({c[1].count})

              </Link>
          )))}
        </Nav.Item>
      </Nav>
    </div>
  )

};
