import React, {useState} from 'react'
import {useRouteMatch} from "react-router-dom";
import {Row, Col, Image} from 'react-bootstrap'
//import PRODUCTS from '../products'
import NotFound from './pages/NotFound';

// function ChangeState(int){
//     this.setState((state) =>{
//         return{imgState: state.imgState = int}
//     });
//     console.log(this.state)
// }

function ProductDetails(props){



    //filter the Array and get the product that we are displaying details for
    let ProdArray = Object.values(props.product)

    const match = useRouteMatch()

    if(match){
        ProdArray = ProdArray.filter(prod => {
            return String(prod.id) === match.params.id
        })
    }

    let prod = ProdArray[0]

    //Changing the picture using React State 
    const [ind, setPicInd] = useState(1);

    if(prod === undefined){
        return(
            <NotFound/>
        )
    }


    return(
        <div className="flex" style={{height: '100vh'}}>
            <br/>
            <br/>
            <br/>
            <h1>{prod.name}</h1>
            <Row>
                <Col md={8} style={{textAlign: 'left'}}>
                    <h2 className="pl-5">${prod.price}</h2>
                    <p className="p-5 text-justify">
                        {prod.description}
                    </p>
                </Col> 
                <Col md={4}>
                    <Row>
                        <Image src={"/media/products/" + prod.filename + "-" + ind + ".png"} fluid rounded/>
                    </Row>
                    <Row style={{display: 'flex', float: 'right'}}>
                        <Image height="50rem" src={"/media/products/" + prod.filename + "-1.png"} className="border" onMouseOver={() => setPicInd(1)} onMouseOut={() => setPicInd(1)}/>


                    </Row>
                    <div className="row" style={{display: 'flex', float: 'right'}}>
                        <div className="col-sm-1 m-2">
                            <img height="50rem" onMouseOver={() => setPicInd(1)} onMouseOut={() => setPicInd(1)} className="border" src={"/media/products/" + prod.filename + "-1.png"} alt="product 1"/>
                        </div>
                        <div className="col-sm-1 m-2">
                            <img height="50rem" onMouseOver={() => setPicInd(2)} onMouseOut={() => setPicInd(1)} className="border" src={"/media/products/" + prod.filename + "-2.png"} alt="product 2"/>
                        </div>
                        <div className="col-sm-1 m-2">
                            <img height="50rem" onMouseOver={() => setPicInd(3)} onMouseOut={() => setPicInd(1)} className="border" src={"/media/products/" + prod.filename + "-3.png"} alt="product 3"/>
                        </div>
                        <div className="col-sm-1 m-2">
                            <img height="50rem" onMouseOver={() => setPicInd(4)} onMouseOut={() => setPicInd(1)} className="border" src={"/media/products/" + prod.filename + "-4.png"} alt="product 4"/>
                        </div>
                    </div>
                </Col>
            </Row>


        </div>
    )
}

export default ProductDetails
