import React from 'react'
import {Card} from 'react-bootstrap'


class ProductCard extends React.Component{
    render(){
        return(
            <div>
                <Card style={{ width: '15rem', color: 'info'}}>
                    <Card.Img variant="top" src={"./media/products/" + this.props.product.filename + "-1.png"} />
                    <Card.Body>
                    <Card.Title>{this.props.product.name}</Card.Title>
                    <Card.Text>
                        ${this.props.product.price}
                    </Card.Text>
                    {/* <Card.Link className="position-absolute" style={{float: "right"}}>Details</Card.Link> */}
                    </Card.Body>
                </Card>
            </div>
        )
    }
}

export default ProductCard;
