import React, { useContext } from 'react'
import * as bs from 'react-bootstrap'
import { Formik, Form, Field} from 'formik'
import AppContext from '../../context'
// import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
// import { loadStripe } from '@stripe/stripe-js';


// const stripePromise = loadStripe(...)


function Checkout(props) {

    // we'll add Stripe's Elements component here later
    return (
        <div sytle={{height: '100vh'}}>
            <h1 className="p-5">
                Checkout
            </h1>
            <CheckoutController/>
        </div>


    )
}
export default Checkout


const CheckoutController = props => {

    return (
        <Formik
            initialValues={{
                name: 'Spencer',
                address1: '123 BYU Ln',
                address2: '',
                city: 'Provo',
                state: 'UT',
                zipcode: '84606',
            }}
            validateOnChange={false}
            validateOnBlur={false}
            validate={values => {
                const errors = {}
                console.log('validating', values)
                return errors
            }}
            onSubmit={async (values, actions) => {
                console.log('submit', values)
                setTimeout(() => {  // wait 2 seconds, then set the form as "not submitting"
                    actions.setSubmitting(false)
                }, 2000)
            }}
        >{form => (
            <PaymentForm form={form}/>
        )}</Formik>
    )
}


/**
 * The form layout/html.
 */

const PaymentForm = props => (
    <Form>
        <bs.Row>
            <bs.Col md={6}>
                <bs.Table borderless size="sm">
                    <thead><tr className="m-4"><th>Shipping</th></tr></thead>
                    <tbody>
                        <tr className="p-1">
                            <td>
                                <Input disable={props.form.isSubmitting} title="Name:" name="name" type="text" placeholder="Name"/>
                            </td>
                        </tr>
                        <tr className="p-1">
                            <td>
                                <Input disable={props.form.isSubmitting} title="Address 1:" name="address1" type="text" placeholder="Address1"/>
                            </td>
                        </tr>
                        <tr className="p-1">
                            <td>
                                <Input disable={props.form.isSubmitting} title="Address 2:" name="address2" type="text" placeholder="Address2"/>
                            </td>
                        </tr>
                        <tr className="p-1">
                            <td>
                                <Input disable={props.form.isSubmitting} title="City:" name="city" type="text" placeholder="City"/>
                            </td>
                        </tr>
                        <tr className="p-1">
                            <td>
                                <Input disable={props.form.isSubmitting} title="State:" name="state" type="text" placeholder="State"/>
                            </td>
                        </tr>
                        <tr className="p-1">
                            <td>
                                <Input disable={props.form.isSubmitting} title="Zip Code:" name="zipcode" type="text" placeholder="ZipCode"/>
                            </td>
                        </tr>
                    </tbody>
                </bs.Table>
            </bs.Col>
            <bs.Col>
                <bs.Table borderless size="sm">
                    <thead><tr className="m-4"><th>Card Information</th></tr></thead>
                    <tbody>
                        <tr>
                            <td>
                                <Input disable={props.form.isSubmitting} title="Card Information:" name="cardinfo" type="text"/>
                            </td>
                        </tr>
                    </tbody>
                </bs.Table>
                <h4>Order Total: ${useContext(AppContext).cartTotal.toFixed(2)}</h4>
                {console.log(props.form.isSubmitting)}
                <bs.Button disabled={props.form.isSubmitting} name="Submit" type="submit" variant="info">
                    {(props.form.isSubmitting ? <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span> : <span></span>)}
                    Submit
                </bs.Button>
            </bs.Col>
        </bs.Row>

    </Form>
)


/**
 * A form input.
 *   props.title - the title that shows above the input box
 *   props.type - the type of input (see React Bootstrap Form.Control)
 *   props.placeholder - placeholder text in the input.
 * This component is finished and doesn't need additional work.
 */
const Input = (props) => (
    <Field name={props.name}>{rProps => (
        <bs.Form.Group>
            <bs.Row>
                {/* <bs.Col md={2} style={{alignContent: 'left'}}>
                    {props.title &&
                        <bs.Form.Label>{props.title}</bs.Form.Label>
                    }
                </bs.Col> */}
                <bs.Col className="m-2">
                    <bs.Form.Control
                        type={props.type}
                        placeholder={props.placeholder}
                        disabled={props.disable}
                        {...rProps.field}
                        //className="p-1"
                    />
                    {rProps.meta.touched && rProps.meta.error &&
                        <div className="text-danger">{rProps.meta.error}</div>
                    }
                </bs.Col>
            </bs.Row>
        </bs.Form.Group>
    )}</Field>
)
