import React from 'react'
import {Card} from 'react-bootstrap'
import {Link} from 'react-router-dom'

class ProductCard extends React.Component{
    render(){
        return(
            <div>
                <Card border="info" className="shadow m-3" style={{ width: '90%'}}>
                    <Link to={"/details/" + this.props.product.id} className="btn btn-info sm position-absolute" style={{left: '66%', width: '33%', MozTextSizeAdjust: 'auto'}}>Details</Link>
                    <Card.Img variant="top" src={"/media/products/" + this.props.product.filename + "-1.png"} />
                    <Card.Header>
                    <Card.Title>{this.props.product.name}</Card.Title>
                    <Card.Text>
                        ${this.props.product.price.toFixed(2)}
                    </Card.Text>
                    </Card.Header>
                </Card>
            </div>
        )
    }
}

export default ProductCard;
