import React, { useContext } from 'react'
import {Table, Image, Button, Col, Row} from 'react-bootstrap'
import AppContext from '../../context'

export default function Cart(props) {

    const context = useContext(AppContext)

    let ProdArray = Object.values(context.products)
    //console.log(context)
    //console.log(context.cart)
    ProdArray = ProdArray.filter(p =>{
        return p.id in context.cart
    })
    //console.log(ProdArray)
    //console.log(context.cart.length)
    if(isEmpty(context.cart)){
        return(
            <div>
                <br></br>
                <br></br>
                <br></br>
                <h1>There is nothing in your cart!</h1>
                <h3>Start shopping!</h3>
            </div>
        )
    }

    let total = 0;

    return(
        <div>
            <h1 className="p-4">Cart</h1>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th></th>
                        <th>Product</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Total</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {ProdArray.map((p) => {
                        let cnt = 0;

                        for (let i of Object.entries(context.cart)){
                            if(p.id.toString() === i[0]){
                                cnt = i[1];
                            }
                        };

                        let subtotal = p.price * cnt;
                        total += subtotal;

                        return(
                            <tr>
                                <td>
                                    <Image id={p.id} style={{height: '3rem'}} src={"/media/products/" + p.filename + "-1.png"} fluid rounded/>
                                </td>
                                <td>{p.name}</td>
                                <td>{p.price}</td>
                                <td>{cnt}</td>
                                <td>{subtotal.toFixed(2)}</td>
                                <td>
                                    <Button 
                                        variant="warning" 
                                        onClick={() => context.removeFromCart(p.id)}>
                                        Remove
                                    </Button>
                                </td>
                            </tr>
                        )
                    })}
                </tbody>

            </Table>
            <Row>
                <Col xs={9}></Col>
                <Col xs={3}><h1>Total: {total.toFixed(2)}</h1></Col>
            </Row>
        </div>
    )
}
