import React, { useContext } from 'react'
import {Nav} from 'react-bootstrap'
import {Link} from 'react-router-dom'
import PRODUCTS from '../products';
import AppContext from '../context'

class Left extends React.Component{
    render(){

      const state = useContext(AppContext)

      const categories = {}
      for (let p of Object.values(state.categories)){
        if(p.category in categories){
          categories[p.category] = categories[p.category] + 1
        }
        else{
          categories[p.category] = 1
        }
      }

      return(
        <div> 
          <Nav defaultActiveKey="/home" className="flex-column">
            <Link to="/" className="nav-link">
              All Products ({Object.values(PRODUCTS).length})
            </Link>
            <Nav.Item>
              {Object.entries(categories).map(([c,count]) => (
                  <Link to={'/category/' + c} key={c} className="nav-link">
                    {c} ({count})
                  </Link>
              ))}
            </Nav.Item>
          </Nav>
        </div>
      )
    }
  }

export default Left;
