import React from 'react'
import {useRouteMatch} from "react-router-dom";
import PRODUCTS from '../products'


function ProductDetails(props){

    let ProdArray = {}
    ProdArray = Object.values(PRODUCTS)

    const match = useRouteMatch()

    let prodName
    let prodDescription
    let prodFile

    if(match){
            ProdArray = ProdArray.filter(prod => {
                prodName = prod.name
                prodDescription = prod.description
                prodFile = prod.filename
                return prod.id === match.params.id
            })
    }

    console.log(ProdArray.name)

    return(
        <div>
            <h1>Product details</h1>
            <br/>
            <br/>
            <h3>{prodName}</h3>
            <div className="row">
                <div className="col-8" style={{textAlign: 'left', textJustify: 'inter-word'}}>
                    <p>
                        {prodDescription}
                    </p>
                </div>
                <div className="col-2">
                    <img src={"/media/products/" + prodFile + "-1.png"}/>
                </div>
            </div>


        </div>
    )
}

export default ProductDetails
