import React from 'react'
import {useRouteMatch} from "react-router-dom";
import PRODUCTS from '../products'


function ProductDetails(props){

    let ProdArray = {}
    ProdArray = Object.values(PRODUCTS)

    const match = useRouteMatch()

    let prodName
    let prodDescription

    if(match){
            ProdArray = ProdArray.filter(prod => {
                prodName = prod.name
                prodDescription = prod.description
                return prod.id === match.params.id
            })
    }

    console.log(ProdArray.name)

    return(
        <div>
            <h1>Product details</h1>
            <br/>
            <br/>
            <h3>{prodName}</h3>
            <div className="row">
                <div className="col-8" style={{textAlign: 'left'}}>
                    <p>
                        {prodDescription}
                    </p>
                    <p>
                        Cillum irure qui amet aliquip officia duis velit eiusmod ipsum labore consequat. Cupidatat proident occaecat irure exercitation elit cillum. Laborum in adipisicing exercitation velit nostrud laboris officia. Pariatur et sunt cillum mollit occaecat deserunt veniam mollit dolor quis adipisicing mollit. Voluptate enim officia nisi mollit elit est deserunt eiusmod. Anim excepteur reprehenderit est elit deserunt fugiat magna eiusmod non consequat et reprehenderit irure.
                    </p>
                </div>
                <div className="col-2">
                    <img src="/media/products/apple-1.png"/>
                </div>
            </div>


        </div>
    )
}

export default ProductDetails
