import React from 'react'
import {Card, Button} from 'react-bootstrap'
import {Link} from 'react-router-dom'

class ProductCard extends React.Component{
    render(){
        return(
            <div>
                <Card border="info" className="m-3" style={{ width: '90%'}}>
                    <Link to={"/details/" + this.props.product.id} className="btn btn-info sm position-absolute top-right">Details</Link>
                    <Card.Img variant="top" src={"/media/products/" + this.props.product.filename + "-1.png"} />
                    <Card.Body>
                    <Card.Title>{this.props.product.name}</Card.Title>
                    <Card.Text>
                        ${this.props.product.price.toFixed(2)}
                    </Card.Text>

                    </Card.Body>

                </Card>
            </div>
        )
    }
}

export default ProductCard;
